#include <stdio.h>

int main(){

    int qtd;
    scanf("%d", &qtd);

    int vet[qtd];
    int value = 0;
    int casa_decimal = 1;

    for(int a = 1; a < qtd; a++)
        casa_decimal *= 10;

    for(int a = 0; a < qtd; a++){
        scanf("%d", &vet[a]);
        value += vet[a] * casa_decimal;
        casa_decimal /= 10;
    }

    printf("%d\n", value);
}

#include <iostream>
using namespace std;

int vetor_para_int(int v[], int tam){
    int i, num = v[0];
    for(i = 1; i < tam; i++) {
        num *= 10;
        num += v[i];
    }
    cout << num << endl;
    return num;
}

int main(){
    int qtd;
    cin >> qtd;
    int v[qtd];

    for(int i =0; i < qtd ; i++){
        cin >> v[i];
    }

    vetor_para_int(v,qtd);

    return 0;
}
