#include <stdio.h>
#include <string.h>

int main(){
    char nome1[100];
    char nome2[100];
    scanf("%s", nome1);    
    scanf("%s", nome2);

    int aux = 0;
    int t1 = strlen(nome1);
    int t2 = strlen(nome2);
    for(int i = 0; i < t1; i++){
        for(int j = 0; j < t2; j++){
            if(nome1[i] == nome2[j]){
                aux++;
                nome2[j] = 0;
                break;
            }
        }
    }
    if(aux == t1){
        printf("sim\n");
    }else{
        printf("nao\n");
    }
}

#include <iostream>
#include <string.h>
using namespace std;

int contar(string str, char letra){
    int cont = 0;
    for(int i = 0; i < (int) str.size(); i++){
        char c = str[i];
        if(c == letra)
            cont++;
    }
    return cont;
}

bool eh_anagrama(string A, string B) {
    if(A.length() != B.length())
        return false;
    for(int i = 0; i < A.size(); i++){
        char c = A[i];
        if(contar(A, c) != contar(B, c))
            return false;
    }
    return true;
}

int main()
{
    string A, B;
    cin >> A >> B;
    if(eh_anagrama(A, B))
        cout << "sim" << endl;
    else {
        cout << "nao" << endl;
    }
}
