#include <stdio.h>
#include <string.h>
int main(){
    char frase[100];
    char busca[50];
    scanf("%[^\n] %[^\n]", frase, busca);
    int x = strlen(frase);
    int y = strlen(busca);
    int contador = 0;
    for(int i = 0; i < x; i++){
        if(frase[i] == busca[0]){
            for(int j = 1; j < y; j++){
                if(frase[i + j] == busca[j]){
                    if(j == (y-1)){
                        contador++;
                    }
                }else{
                    break;
                }
            }
        }
    }
    printf("%d\n", contador);
}

#include <iostream>
using namespace std;

int n_substring(string menor, string maior)
{
    int pos = 0;
    int cont = 0;
    pos = maior.find(menor);
    while(pos != (int)string::npos){
        cont++;
        pos = maior.find(menor, pos + 1);
    }
    return cont;
}

int main(){
    string stra, strb;
    getline(cin, stra);
    getline(cin, strb);
    cout << n_substring(strb, stra) << endl;
    return 0;
}
